import react, { useState } from 'react';
import ReactDom, { createRoot } from 'react-dom/client';

const root=createRoot(document.getElementById('root'));

function ContactForm(){
    // let[name,setname]=useState('old faculty');

    // let faculties=['chetan','nenis'];
    let[faculties,setfaculty]=useState(['chetan','nenis']);
    let formtedfaculty=faculties.map((fac)=>{
        return <li>{fac}</li>;
    });
    return (
        <>
        <form>
            <label>Enter your name  : </label>
            <input type='text' onChange={(e)=>{
            //    setname('new name')
            // setname(e.target.value);
            // console.log(e.target.value);
            //... this operator to called sprad operator 
            ;
            }}></input>
            <br></br> 
            <br></br>
            <button onChange={(e)=>{
                setfaculty([...faculties,e.target.value]);
                ;
            }}>SUBMIT</button>
        </form>
        <br></br>
        {/* <h1>{name}</h1> */}
        <ul>
            {formtedfaculty}
        </ul>
        </>
    );

}
root.render(
    <ContactForm/>
);
